import "./CostItem.css";
import { useState } from "react";

function CostItem({ props }) {

  const [name, setName] = useState(props.name)

  const ChangeClickHandler = () => {
    setName("зяблик");
  }

  return (
    <div class="container">
      <div class="row">
        <div class="product-name">{name}</div>
        <div class="price-name">{props.price}$</div>
      </div>
      <div className="row">
      <div class="date-name">{props.date.toLocaleDateString()}</div>
      <button onClick={ChangeClickHandler} class="button">123</button>
      </div>
    </div>
  );
}

export default CostItem;
