import CostItem from "../CostItem/CostItem";
import CostFilter from "../CostFilter/CostFilter";
import { useState } from "react";

function Costs(props) {
  const [selectedYear, setSelectedYear] = useState("All");

  const yearChangeHandler = (year) => {
    setSelectedYear(year);
    RenderCostItem(year);
  };

  const RenderCostItem = (year) => {
    const FilteredItems = props.items
      .filter((item) => year === "All" || item.date.getFullYear() == year)
      .map((item) => <CostItem key={item.id} props={item} />);

      if(FilteredItems.length)
      return <p>Список пуст. Давай шекели!!!</p>

      return FilteredItems
  };

  return (
    <>
      <CostFilter items={props.items} onChangeYear={yearChangeHandler} />
      {RenderCostItem(selectedYear)}
    </>
  );
}

export default Costs;
