import "./CostForm.css";
import { useState } from "react";

function CostForm() {
  const [name, setName] = useState("");
  const [sum, setSum] = useState("");
  const [date, setDate] = useState("");

  const [disabled, setDisabled] = useState(true);

  const changeName = (e) => {
    checkDataComplete();
    setName(e.target.value);
  };
  const changeSum = (e) => {
    checkDataComplete();
    setSum(e.target.value);
  };
  const changeDate = (e) => {
    checkDataComplete();
    setDate(e.target.value);
  };

  const checkDataComplete = () => {
    if (name && sum && date) 
      setDisabled(false);
    else 
      setDisabled(true);

      console.log(disabled)
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const costData = {
      name: name,
      sum: sum,
      date: new Date(date),
    };

    setName("");
    setSum("");
    setDate("");

    console.log(costData)
  };

  return (
    <form onSubmit={submitHandler}>
      <div className="costForm_items">
        <div className="costForm_item">
          <label className="costItem_Name">Название</label>
          <input
            className="costItem_Input"
            type="text"
            placeholder="Введите название"
            onChange={changeName}
            value={name}
          />
        </div>
        <div className="costForm_item">
          <label className="costItem_Name">Сумма</label>
          <input
            className="costItem_Input"
            type="number"
            min="1"
            placeholder="Введите сумму"
            onChange={changeSum}
            value={sum}
          />
        </div>
        <div className="costForm_item">
          <label className="costItem_Name">Дата</label>
          <input
            className="costItem_Input"
            type="date"
            min="2023-01-01"
            placeholder="Введите дату"
            onChange={changeDate}
            value={date}
          />
        </div>
        <div className="costForm_submit">
          <button disabled={disabled} type="submit">Добавить расход</button>
        </div>
      </div>
    </form>
  );
}

export default CostForm;
